 if ( ! defined( 'ABSPATH' ) ) { exit; } class FS_Contact_Form_Manager { private static $_instance; static function instance() { if ( ! isset( self::$_instance ) ) { self::$_instance = new FS_Contact_Form_Manager(); } return self::$_instance; } private function __construct() { } public function get_query_params( Freemius $fs ) { $context_params = array( 'plugin_id' => $fs->get_id(), 'plugin_public_key' => $fs->get_public_key(), 'plugin_version' => $fs->get_plugin_version(), ); if ( $fs->is_registered() ) { $context_params = array_merge( $context_params, FS_Security::instance()->get_context_params( $fs->get_site(), time(), 'contact' ) ); } return array_merge( $_GET, array_merge( $context_params, array( 'plugin_version' => $fs->get_plugin_version(), 'wp_login_url' => wp_login_url(), 'site_url' => Freemius::get_unfiltered_site_url(), ) ) ); } public function get_standalone_link( Freemius $fs ) { $query_params = $this->get_query_params( $fs ); $query_params['is_standalone'] = 'true'; $query_params['parent_url'] = admin_url( add_query_arg( null, null ) ); return WP_FS__ADDRESS . '/contact/?' . http_build_query( $query_params ); } }